package com.sn30.suwonuniv.info.suwonmate_native.viewimport android.content.Intentimport android.os.Bundleimport android.util.AttributeSetimport android.view.Gravityimport android.view.Viewimport android.widget.Buttonimport android.widget.TextViewimport androidx.appcompat.app.AppCompatActivityimport androidx.appcompat.widget.SwitchCompatimport com.google.android.material.slider.Sliderimport com.sn30.suwonuniv.info.suwonmate_native.BuildConfigimport com.sn30.suwonuniv.info.suwonmate_native.Rimport com.sn30.suwonuniv.info.suwonmate_native.databinding.SettingsBindingimport com.sn30.suwonuniv.info.suwonmate_native.models.SettingStoreclass SettingsActivity: AppCompatActivity() {    private lateinit var pref: SettingStore    private lateinit var binding: SettingsBinding    override fun onCreate(savedInstanceState: Bundle?) {        pref = SettingStore(this)        super.onCreate(savedInstanceState)        binding = SettingsBinding.inflate(layoutInflater)        setContentView(binding.root)        supportActionBar?.setDisplayHomeAsUpEnabled(true)        val studentCard = binding.studentSetting        studentCard.title.text = "학생 정보"        studentCard.titleIcon.setImageResource(R.drawable.ic_school)        val textView = TextView(this)        textView.text = "개설 강좌 메뉴에서 기본으로 보여질 학부 및 학년을 선택합니다."        studentCard.contents.addView(textView)        val dataSaveMode = SwitchCompat(this)        dataSaveMode.text = "데이터 절약 모드"        dataSaveMode.isChecked = pref.getBool("dataSaver", false)        dataSaveMode.setOnCheckedChangeListener { _, isChecked ->                pref.setBool("dataSaver", isChecked)        }        val liveSearchSlider = Slider(this)        val liveSearch = SwitchCompat(this)        liveSearch.text = "입력하여 바로 검색"        liveSearch.isChecked = pref.getBool("liveSearch", false)        liveSearch.setOnCheckedChangeListener { _, isChecked ->            pref.setBool("liveSearch", isChecked)            if (isChecked) liveSearchSlider.visibility = View.VISIBLE else liveSearchSlider.visibility = View.GONE        }        liveSearchSlider.visibility = if (liveSearch.isChecked) View.VISIBLE else View.GONE        liveSearchSlider.value = pref.getInt("liveSearchCount", 0).toFloat()        liveSearchSlider.valueFrom = 0.0F        liveSearchSlider.valueTo = 3.0F        liveSearchSlider.stepSize = 1.0F        liveSearchSlider.addOnChangeListener { _, value, _ ->            pref.setInt("liveSearchCount", value.toInt())        }        binding.functionSettings.titleIcon.setImageResource(R.drawable.ic_settings)        binding.functionSettings.title.text = "기능 설정"        binding.functionSettings.contents.addView(dataSaveMode)        binding.functionSettings.contents.addView(liveSearch)        binding.functionSettings.contents.addView(liveSearchSlider)        binding.resetMenu.titleIcon.setImageResource(R.drawable.ic_reset)        binding.resetMenu.title.text = "초기화 메뉴"        binding.versionInfo.titleIcon.setImageResource(R.drawable.ic_info)        binding.versionInfo.title.text = "버전 정보"        val versionInfoMessage = TextView(this)        versionInfoMessage.text = "앱 버전: ${BuildConfig.VERSION_NAME}\nDB 버전: 알 수 없음"        binding.versionInfo.contents.addView(versionInfoMessage)        binding.emailButton.setOnClickListener {            val sendMail = Intent(Intent.ACTION_SEND)            sendMail.type = "text/plain"            sendMail.setPackage("com.google.android.gm")            sendMail.putExtra(Intent.EXTRA_EMAIL, "orgsun30812+suwon_mate@gmail.com")            sendMail.putExtra(Intent.EXTRA_SUBJECT, "수원 메이트(네이티브) 버그 및 건의")            startActivity(sendMail)        }    }}